CREATE TABLE comments (id UUID DEFAULT gen_random_uuid() PRIMARY KEY, content TEXT NOT NULL, poll_id UUID NOT NULL REFERENCES polls(id) ON DELETE CASCADE, user_email TEXT NOT NULL REFERENCES users(email) ON DELETE CASCADE, created_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL, updated_at TIMESTAMP WITH TIME ZONE DEFAULT timezone('utc'::text, now()) NOT NULL); CREATE INDEX comments_poll_id_idx ON comments(poll_id); CREATE INDEX comments_user_email_idx ON comments(user_email); ALTER TABLE comments ENABLE ROW LEVEL SECURITY; CREATE POLICY "댓글 조회 가능" ON comments FOR SELECT USING (true); CREATE POLICY "인증된 사용자만 댓글 작성 가능" ON comments FOR INSERT WITH CHECK (auth.role() = 'authenticated'); CREATE POLICY "자신의 댓글만 수정 가능" ON comments FOR UPDATE USING (auth.email() = user_email); CREATE POLICY "자신의 댓글만 삭제 가능" ON comments FOR DELETE USING (auth.email() = user_email);
